import { Squeeze as Hamburger } from 'hamburger-react';
import { signOut, useSession } from 'next-auth/react';
import React, { useEffect, useState } from 'react';
import { GrSpotify } from 'react-icons/gr';
import { IoLogOutOutline } from 'react-icons/io5';
import { useSpotify } from '../../hooks/useSpotify';

import styles from './styles.module.scss';

type Artists = {
  name: string
}
interface TrackProps {
  name: string;
  artists: [
    name: string
  ];
}

export function Header() {
  const { data: session } = useSession();
  const [songData, setSongData] = useState<TrackProps | null>();
  const [isMenuShowing, setIsMenuShowing] = useState(false);

  const spotifyAPI = useSpotify();

  async function getNowPlaying() {
    if (spotifyAPI.getAccessToken()) {
      const { item } = await fetch('https://api.spotify.com/v1/me/player/currently-playing', {
        headers: {
          Authorization: `Bearer ${spotifyAPI.getAccessToken()}`
        }
      })
      .then((response) => response.json())
      if (item) {
        setSongData({
          name: item.name,
          artists: item.artists.map((artist: Artists) => artist.name)
        })
      }

      return;
    }
  }

  useEffect(() => {
    getNowPlaying();
  }, [session, spotifyAPI]);

  return (
    <header className={styles.header}>
      <div className={styles.headerContent}>
        <div className={styles.currentPlaying}>
          <GrSpotify />
          {songData ? (
            <p>
              {songData.name}
              {' '}
              <span>
                -
                {' '}
                {songData.artists}
              </span>
            </p>
          ) : (
            <p>
              Spotify
              {' '}
              <span>
                -
                Nada Tocando
              </span>
            </p>
          )}

        </div>

        <div className={styles.userInfo}>
          <ul className={isMenuShowing ? styles.active : ''}>
            <li>
              <span>{session?.user?.name}</span>
            </li>

            <li>
              <img src={session?.user?.image ? session.user.image : 'data:image/png;base64,'} alt="Foto do UsuÃ¡rio" />
            </li>
          
            <li>
              <button onClick={() => signOut()} type="button">
                <IoLogOutOutline />
              </button>
            </li>
          </ul>

          <div className={styles.menu}>
            <Hamburger toggled={isMenuShowing} toggle={setIsMenuShowing} />
          </div>
        </div>
      </div>
    </header>
  );
}
